import React, {useState, useEffect } from 'react'
import { LinkContainer } from "react-router-bootstrap";
import Nav from 'react-bootstrap/Nav';

// get profile to this page too!
function NavBar({profile}) {

  const [prof, setProf] = useState("")
  useEffect(()=> {
    const storedProfile = localStorage.getItem('profile');
    if (storedProfile) {
      setProf(storedProfile);
    }
  }, [])

  useEffect(()=> {
    localStorage.setItem('profile', profile);
  }, [profile])
  return (
      <Nav variant="pills" style={{zIndex: '10', backgroundColor: '#3AAFA9', position:'sticky', top: '0', width: '100%', display:'flex', justifyContent: 'space-between', padding: '1% 5%'}}>
        <div style = {{paddingLeft: '5%'}}>
          <Nav.Item>
            <LinkContainer style={{backgroundColor: '#3AAFA9', color: 'white', fontSize: '100%'}} to = {`/${prof}`}>
              <Nav.Link ><span><b>A. MANI DEEPAK</b></span></Nav.Link>
            </LinkContainer>
          </Nav.Item>
        </div>
        <div style={{display: 'flex'}}>
          <Nav.Item>
            <LinkContainer style={{backgroundColor: '#000000', color: '#FEFFFF'}} to = {`/${prof}`}>
            <Nav.Link >Home</Nav.Link>
            </LinkContainer>
          </Nav.Item>
          <Nav.Item>
            <LinkContainer style={{backgroundColor: '#000000', color: '#FEFFFF'}} to={`/${prof}/projects`}>
            <Nav.Link >Projects</Nav.Link>
            </LinkContainer>
          </Nav.Item>
          <Nav.Item>
            <LinkContainer style={{backgroundColor: '#000000', color: '#FEFFFF'}} to={`/${prof}/about`}>
            <Nav.Link >
              About
            </Nav.Link>
            </LinkContainer>
          </Nav.Item>
          <Nav.Item>
            <LinkContainer style={{backgroundColor: '#3AAFA9', border: '1px solid #000000', color: '#FEFFFF'}} to={`/${prof}/resume`}>
            <Nav.Link >
              Resume
            </Nav.Link>
            </LinkContainer>
          </Nav.Item>
        </div>

      </Nav>
  );
}

export default NavBar
