import React, {useState, useEffect} from "react";
import { useParams } from "react-router-dom";
import {Document, Page} from 'react-pdf/dist/esm/entry.webpack'
import "react-pdf/dist/esm/Page/TextLayer.css";
// import './Resume.css'


function Resume() {
  let {profile} = useParams()
  const [numPages, setNumPages] = useState(null);
	const [pageNumber, setPageNumber] = useState(1);

	const onDocumentLoadSuccess = ({ numPages }) => {
		setNumPages(numPages);
	};

  return (
    <div style={{justifyContent:'space-around', display: 'flex', padding: '5%'}}>

        <Document
            file={`/data/${profile}/${profile}.pdf`}
            onLoadSuccess={onDocumentLoadSuccess}
        >
            <Page pageNumber={pageNumber} height={1200}/>

        </Document>
        <a href={require(`./../../public/data/${profile}/${profile}.pdf`)} download={`${profile}_resume`}><svg stroke="currentColor" fill="currentColor" stroke-width="0" viewBox="0 0 512 512" height="2em" width="2em" xmlns="http://www.w3.org/2000/svg"><path d="M216 0h80c13.3 0 24 10.7 24 24v168h87.7c17.8 0 26.7 21.5 14.1 34.1L269.7 378.3c-7.5 7.5-19.8 7.5-27.3 0L90.1 226.1c-12.6-12.6-3.7-34.1 14.1-34.1H192V24c0-13.3 10.7-24 24-24zm296 376v112c0 13.3-10.7 24-24 24H24c-13.3 0-24-10.7-24-24V376c0-13.3 10.7-24 24-24h146.7l49 49c20.1 20.1 52.5 20.1 72.6 0l49-49H488c13.3 0 24 10.7 24 24zm-124 88c0-11-9-20-20-20s-20 9-20 20 9 20 20 20 20-9 20-20zm64 0c0-11-9-20-20-20s-20 9-20 20 9 20 20 20 20-9 20-20z"></path>Download file</svg></a>


</div>
  );
};

export default Resume;
